import React from 'react'
import { Route, Routes } from 'react-router-dom';
import { Header, Footer } from './components';
import { Catalog, Home, Complex } from './pages';
import axios from 'axios';

import './App.css';

function App() {

  const [complexes, setСomplexes] = React.useState([]);

  const [error, setError] = React.useState(null);
  const [isLoaded, setIsLoaded] = React.useState(false);
  const [items, setItems] = React.useState([]);

  // Примечание: пустой массив зависимостей [] означает, что
  // этот useEffect будет запущен один раз
  // аналогично componentDidMount()
  React.useEffect(() => {
    fetch("http://45.135.164.103:8080/api/category")
      .then(res => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setItems(result);
        },
        // Примечание: важно обрабатывать ошибки именно здесь, а не в блоке catch(),
        // чтобы не перехватывать исключения из ошибок в самих компонентах.
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      )
  }, []);

  console.log(items);

  React.useEffect(() => {
    setСomplexes(
      {
        "complexes": [
          {
            "id": 0,
            "imageUrl": "../assets/img/photo.jpg",
            "name": "Nordic Spa",
            "slug": "nordic-spa",
            "place": "Калининградская область, посёлок городского типа Янтарный",
            "rating": 4.3,
            "reviews": 12,
            "category": ["Семейная", "Русская баня", "Номерная", "Оплата наличными"],
            "price": 1000,
            'telephone_price': 1100,
            "discount": 10,
          },
          {
            "id": 1,
            "tag": 1,
            "imageUrl": "../assets/img/photo.jpg",
            "name": "Королевская баня",
            "slug": "korolevskaya-banya",
            "place": "Москва, Внуково",
            "rating": 4.5,
            "reviews": 2,
            "category": ["Русская баня", "Номерная", "Оплата наличными"],
            "price": 1200,
            'telephone_price': 1500,
            "discount": 15,
          },
        ],
      });
    }, []);

  return (

    <div className="fullpage App">

      <Header />
      <div className="main-content">
        <Routes>
          <Route path="/" element={<Home />} exact />
          <Route path="/catalog" element={<Catalog items={complexes.complexes} />} exact />
          <Route path="/complex/:id" element={<Complex />} />
        </Routes>
      </div>

      <Footer />

    </div>

  );
}

export default App;
