import React from 'react';
import {Link} from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';

function Pagination( {props} ){

  const dispatch = useDispatch();
  const currentPage = useSelector(state => state.baths.currentPage);

  console.log(currentPage);

  const nextPage = (page) => {
    dispatch({type: 'CHANGE_PAGE', page: page });
    window.scrollBy({
        top: 0,
        behavior: 'smooth'
    });
  }

  function getLinkPagintaion( item, index ){

    if ( item.label == '&laquo; Previous' ){

      return (<li><a href={`#page=${item.label}`} className="item item-prev"><svg width="16" height="16" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg"><path d="M10 3L5 8L10 13" stroke="black" strokeWidth="1.6" strokeLinecap="round" strokeLinejoin="round"/></svg></a></li>);

    }else if( item.label == 'Next &raquo;' ){

      return (<li><a href={`#page=${item.label}`} className="item item-next" ><svg width="16" height="16" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg"><path d="M6 13L11 8L6 3" stroke="black" strokeWidth="1.6" strokeLinecap="round" strokeLinejoin="round"/></svg></a></li>);

    }else if (item.label == '...'){

      return (<li><a href="#" className="item item-dots">...</a></li>);

    }else{

      return (<li><a href={`#page=${item.label}`} onClick={ () => nextPage(item.label) } className="item" key={`${item.id}_${item.label}`}>{item.label}</a></li>);

    }

  }

  const links = props.links;

	return(
		<div className="pagination">


      <ul className="pagination-container">
        {

          links && links.map( (item, index) => (

            getLinkPagintaion(item, index)

          ) )

        }
      </ul>
    </div>
	);
}

export default Pagination;
