const defaultState = {
  currentPage: 1,
}

export const bathsReducer = (state = defaultState, action) => {

  switch (action.type){
    case "NEXT_PAGE": 
      return { ...state, currentPage: state.currentPage + action.page }
    case "PREV_PAGE":
      return { ...state, currentPage: state.currentPage - action.page }
    case "CHANGE_PAGE":
      return { ...state, currentPage: action.page }
    default:
      return state
  }
}
