import React from 'react';
import {Link} from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';

function Pagination( {props} ){

  const dispatch = useDispatch();
  const currentPage = useSelector(state => state.baths.currentPage);

  console.log(currentPage);

  const nextPage = (page) => {
    dispatch({type: 'NEXT_PAGE', page: page });
  }

  const links = props.links;

	return(
		<div className="pagination">


      <ul className="pagination-container">
        {

          links && links.map( (item, index) => (

            <li><a href={`#page=${item.label}`} onClick={ () => nextPage(item.label) } className="item" key={`${item.id}_${item.label}`}>{item.label}</a></li>

          ) )

        }
