import React from 'react';
import {Link} from 'react-router-dom';

function Breadcrumb( {items} ){


    function getItemBredcrumbs( breadcrumb, index ){

        if( breadcrumb.link != '' || !(breadcrumb.link) ){
            return ( <span key={`${breadcrumb.name}_${breadcrumb.index}`}><Link to={breadcrumb.link} >{breadcrumb.title}</Link>&nbsp;/&nbsp;</span>);
        }else{
            return ( <span key={`${breadcrumb.name}_${breadcrumb.index}`}>{breadcrumb.title}</span>);
        }

    }

	return(
		<nav className="breadcrumb breadcrumb-gray">

            {
                items && items.map( (breadcrumb, index) => (

                    getItemBredcrumbs(breadcrumb, index)

                    )

                ) 
            }

        </nav>
	);
}

export default Breadcrumb;
